import React from 'react'
import{useEffect,useState} from 'react'
import { Row,Col, Container} from 'react-bootstrap'
import Workoutform from '../components/Workoutform'
import Fetchworkout from '../components/Fetchworkout'
import{useDispatch,useSelector} from 'react-redux'
import {setWorkout} from '../Redux/Action'
import {memo} from 'react'

function Home(){
  //code using useState hook
 // const[workout,setWorkout]= useState()
  // const fetchWorkout = async() =>  {
  //   const response = await fetch('http://localhost:4000/api/workout')
  //   const jsondata =  await response.json()
  //   setWorkout(jsondata)
  //   }
  // useEffect(()=>{fetchWorkout()},[])

  const dispatch = useDispatch();
    const fetchProduct = async()=> {
    const response = await fetch('http://localhost:4000/api/workout');
    const jsonData = await response.json();
    dispatch(setWorkout(jsonData));         
    }

    useEffect(() => {fetchProduct()},[]);

    const workout = useSelector(state=>state.allWorkout)
    console.log(workout)

  return (
    <>
    <Container className='align-items-center'>
      <h1>Excercise Monitor App</h1>
    <Row className='m-5'>
    <Col>
    { workout && workout.slice().reverse().map((item=>(
       <Fetchworkout 
       id= {item._id}
       title={item.title} 
       reps={item.reps} 
       load={item.load} 
       date={item.createdAt} 
       />
      )))} 

    </Col>

    <Col>
    <Workoutform/>
    </Col>
    </Row>
    </Container>
    </>
  )
}
export default memo(Home)
