import React from 'react'
import {Button} from 'react-bootstrap'
import {useState,useEffect} from 'react'


function Fetchworkout({workout,setWorkout,id}) {

  const handelDel = async()=>{ 
    const response = await fetch('http://localhost:4000/api/workout/' + id,{
    method :'DELETE'
  })
    const json = await response.json()
    const filterData = await workout.filter((w)=> w._id !== json._id)
    setWorkout(filterData)
  }

console.log(workout)

  return (

    <div className='border shadow col-lg-8 col-sm-12 mx-auto m-3'  style={{backgroundColor:'pink',}}>
    { workout && workout.slice().reverse().map((item=>(

    <div>
    <h1>{item.title}</h1>
    <p><strong>Rep - </strong>{item.reps}</p>
    <p><strong>Load(Kg) -</strong> {item.load}</p>
    <p>{item.createdAt}</p>
    <Button onClick={handelDel}>Delete</Button>
    </div>

    )
    ))}

    </div>

  )
}

export default Fetchworkout
