const initialState = [];

export const ProductReducers = (state = initialState, action) => {
switch(action.type){
		case "SET_PRODUCTS": 
		return [ ...action.payload];
		default: 
		return state;
	}
};
export const getAddItem = (state= [], action) => {
	switch(action.type){
		case "REMOVEITEM":
		return state.filter((item) => {
		return item.id !== action.payload.id
		});
}
return state;
}
