import React from 'react'
import {Button} from 'react-bootstrap'
import { AiFillDelete } from 'react-icons/ai';
// date fns
import {formatDistance} from 'date-fns'





function Fetchworkout({workout,setWorkout,id,reps,load,title,date}) {

  const handelDel = async()=>{ 


    const response = await fetch('http://localhost:4000/api/workout/' + id,{

    method :'DELETE'
  })
    const json = await response.json()
    const filterData = await workout.filter((w)=> w._id !== json._id)
    setWorkout(filterData)
  }


  return (

    <div className='border shadow col-lg-8 col-sm-12 mx-auto m-3'  style={{backgroundColor:'grey'}}>


    <div>
    <h1>{title}</h1>
    <p><strong>Rep - </strong>{reps}</p>
    <p><strong>Load(Kg) -</strong> {load}</p>
    <p>{formatDistance( new Date(date), { addSuffix: true })}</p>
    <Button variant= 'danger'  onClick={handelDel}><AiFillDelete/></Button>
    </div>


    </div>

  )
}

export default Fetchworkout
