const initialState = [];

export const ProductReducers = (state = initialState, action) => {
switch(action.type){
		case "SET_PRODUCTS": 
		return [ ...action.payload];

				}
switch(action.type){
		case "REMOVEIM":
		return state.filter((item) => {
		return item.id !== action.payload.id
		});
		default: 
		return state;
					}
return state;

}
