import React from 'react'
import { useState } from 'react'
import {Form,Button} from 'react-bootstrap'
import {Link} from 'react-router-dom'

function Login() {
    const[login,setLogin]= useState(
        {
            email:'',
            password:''
        }
    )

    const handelChange =(e) =>{
        setLogin({...login,[e.target.name]:e.target.value})

    }
console.log(login)
  return (
    <>
    <h1 className='text-center'>Login Form</h1>
    <Form className='col-lg-6 col-sm-12 mx-auto border shadow p-5'>
     <Form.Label>Email</Form.Label>
     <Form.Control type="email" value={login.email} name="email" onChange={handelChange}/>
     <Form.Label>Password</Form.Label>
     <Form.Control type="password" value={login.password} name="password" onChange={handelChange}/>
     <br/>
     <Button>Submit</Button>
     <Button as={Link} to={'/signup'}>Signup</Button>
    </Form>
    </>
  )
}

export default Login
