import React from 'react'
import { useState } from 'react'
import {Form,Button} from 'react-bootstrap'
import {Link} from 'react-router-dom'

function Login() {
    const[login,setLogin]= useState(
        {
            email:'',
            password:''
        }
    )

    const handelChange =(e) =>{
        setLogin({...login,[e.target.name]:e.target.value})

    }
    const handelSubmit = async()=>{
      const response = await fetch('http://localhost:4000/api/user/login/',{
          method:'POST',
          body:JSON.stringify(login),
          headers:{
              'Content-Type':'application/json' 
          }
      })
      const jsonData = await response.json()
      console.log(jsonData)
      if(jsonData.error){
          setError(jsonData.error)
      }
      if(!jsonData.error){
          setError('')
          localStorage.setItem('user', JSON.stringify(jsonData))
          alert('Record Added')
          navigate('/login')
          dispatch(login(jsonData))
      }

  }


  return (
    <>
    <h1 className='text-center'>Login Form</h1>
    <Form className='col-lg-6 col-sm-12 mx-auto border shadow p-5'>
     <Form.Label>Email</Form.Label>
     <Form.Control type="email" value={login.email} name="email" onChange={handelChange}/>
     <Form.Label>Password</Form.Label>
     <Form.Control type="password" value={login.password} name="password" onChange={handelChange}/>
     <br/>
     <Button onClick={handelSubmit}>Submit</Button>
     <Button as={Link} to={'/signup'} className='mx-2'>Signup</Button>
    </Form>
    </>
  )
}

export default Login
