   console.log(workout)

  return (
    <>
    <Container>
    <Row className='m-5'>
    <Col>
    { workout && workout.slice().reverse().map((item=>(
       <Fetchworkout workout={item} />
      )))} 
      {/* <Fetchworkout workout={item} id= {item._id} item={item} title={item.title} reps={item.reps} load={item.load} date={item.createdAt} setWorkout= {setWorkout}/>
      )))} */} 


    </Col>

    <Col>
    <Workoutform/>
    </Col>
    </Row>
    </Container>
    </>
  )
}
export default Home
