import React from 'react'
import {Button} from 'react-bootstrap'
import {useState,useEffect} from 'react'


function Fetchworkout({workout,setWorkout}) {

  const handelDel = async()=>{ 
    const response = await fetch('http://localhost:4000/api/workout/' + workout._id)
    const json = await response.json()
    console.log(json._id)
    const filterData = workout.filter((w)=> w._id !== json._id)
    setWorkout(filterData)
  }

  // useEffect(()=>{
  //   handelDel()
  // },[]) 

  console.log(workout)
  return (
    <>
    <div className='border shadow col-lg-8 col-sm-12 mx-auto m-3'  style={{backgroundColor:'pink',}}>
    <h1 key = {workout._id}>{workout.title}</h1>
    <p><strong>Rep - </strong>{workout.reps}</p>
    <p><strong>Load(Kg) -</strong> {workout.load}</p>
    <p>{workout.createdAt}</p>
    <Button onClick={handelDel}>Delete</Button>
    </div>

    </>
  )
}

export default Fetchworkout
