import React, { useState } from 'react'
import {Button} from 'react-bootstrap'
import { AiFillDelete } from 'react-icons/ai';
import {useSelector,useDispatch} from'react-redux'
import {increaseItem , removeItem, decreaseItem, emptyCart} from '../Redux/Action';

// date fns
import {formatDistanceToNow} from 'date-fns'


function Fetchworkout({workout}) {


   const workouts = useSelector(state => state.allProducts);
   console.log(workouts)
  const dispatch = useDispatch()
  const handelDel = async()=>{ 
    const response = await fetch('http://localhost:4000/api/workout/' + workout._id,{
    method :'DELETE'
  })
   const json = await response.json()
  //   const filterData = await workout.filter((w)=> w._id !== json._id)
  //  setWorkout(filterData)
  }



  return (

    <div className='border shadow col-lg-8 col-sm-12 mx-auto m-3'  style={{backgroundColor:'grey'}}>
    <div>
    <h1>{workout.title}</h1>
    <p><strong>Rep - </strong>{workout.reps}</p>
    <p><strong>Load(Kg) -</strong> {workout.load}</p>
    {/* <p>{formatDistanceToNow( new Date(date), { addSuffix: true })}</p> */}
    <Button variant= 'danger'  onClick={handelDel}><AiFillDelete/></Button>
    <Button variant= 'danger'  onClick={()=> dispatch(removeItem('json'))}><AiFillDelete/></Button>
    </div>


    </div>

  )
}

export default Fetchworkout
